        def cariKamar(self):
                keyword = self.lineEdit.text().lower().strip()


                # Buka halaman sesuai keyword
                if keyword == "biasa":
                        self.MainWindow.close()
                        self.bukaDetailBiasa()
                        return

                elif keyword == "vip":
                        self.MainWindow.close()
                        self.bukaDetailVip()
                        return

                elif keyword == "vvip":
                        self.MainWindow.close()
                        self.bukaDetailVvip()
                        return

                # Jika keyword tidak dikenal
                QMessageBox.warning(None, "Tidak ditemukan", f"Kamar '{keyword}' tidak ada.")


        
